import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Spinner } from "react-bootstrap";

export default function Detail() {
  const { id } = useParams();
  const [pjDetail, setPjDetail] = useState({});
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    axios
      .get(`https://rickandmortyapi.com/api/character/${id}`)
      .then(({ data }) => {
        if (data.name) {
          setPjDetail(data);
        } else {
          window.alert("No hay personajes con ese ID");
        }
        setIsLoading(false);
      })
      .catch((err) => {
        window.alert("Error");
        setIsLoading(false);
      });

    return () => {
      console.log("Me desmonto, adios!");
    };
  }, [id]);

  if (isLoading) {
    return <Spinner animation="border" />;
  }

  return (
    <div>
      <h3>{pjDetail.name && pjDetail.name}</h3>
      <h5>{pjDetail.status ? pjDetail.status : ":( no hay status"}</h5>
      <img src={pjDetail.image} alt={pjDetail.name} />
      <section>
        <span>üêû{pjDetail.species}</span>
        <span> ‚ôÄ {pjDetail.gender}</span>
        <span>üåç {pjDetail.origin?.name}</span>
      </section>
    </div>
  );
}
